INSERT INTO rezept_form (kategorie, name, zutaten, zubereitung, bild) VALUES
    ('Hauptspeise', 'Klassische Spaghetti Carbonara', '500g Spaghetti, 200g Guanciale oder Pancetta, 4 frische Eigelb, 100g Pecorino Romano, frisch gemahlener schwarzer Pfeffer', 'Zuerst die Spaghetti in reichlich Salzwasser al dente kochen. Währenddessen den Speck in einer Pfanne ohne zusätzliches Fett knusprig anbraten. In einer Schüssel Eigelb mit dem geriebenen Käse und viel Pfeffer vermengen. Die Nudeln direkt aus dem Wasser zum Speck geben, die Pfanne vom Herd nehmen und die Ei-Käse-Mischung unter ständigem Rühren unterheben, bis eine cremige Sauce entsteht.', NULL),
    ('Vorspeise', 'Tomaten-Bruschetta mit Knoblauch', '1 Ciabatta, 500g reife Tomaten, 2 Knoblauchzehen, 1 Bund Basilikum, 50 EL Olivenöl, Salz, Pfeffer', 'Die Tomaten entkernen und in sehr feine Würfel schneiden. Knoblauch fein hacken und mit den Tomaten, gezupftem Basilikum und Olivenöl vermengen. Mindestens 15 Minuten ziehen lassen. Das Ciabatta in Scheiben schneiden, im Ofen oder in der Pfanne goldbraun rösten und die Tomatenmischung großzügig darauf verteilen. Mit etwas grobem Salz vollenden.', NULL),
    ('Nachspeise', 'Mousse au Chocolat traditionell', '200g Zartbitterschokolade (mind. 70%), 3 Bio-Eier, 200ml Sahne, 1 Prise Salz, 20g Zucker', 'Die Schokolade im Wasserbad langsam schmelzen und anschließend leicht abkühlen lassen. Die Eier trennen. Das Eiweiß mit einer Prise Salz sehr steif schlagen. Die Sahne ebenfalls steif schlagen. Zuerst das Eigelb unter die flüssige Schokolade rühren, dann vorsichtig den Eischnee und zum Schluss die Sahne unterheben. Die Mousse für mindestens 4 Stunden im Kühlschrank fest werden lassen.', NULL),
    ('Hauptspeise', 'Rotes Linsen-Curry (Dal)', '300g rote Linsen, 1 Dose Kokosmilch, 2 Zwiebeln, 3 cm Ingwer, Kurkuma, Kreuzkümmel, Koriander, frischer Spinat', 'Zwiebeln und Ingwer fein würfeln und in Öl glasig dünsten. Die Gewürze kurz mit anrösten, bis sie duften. Die gewaschenen Linsen hinzugeben und mit Kokosmilch und etwas Wasser ablöschen. Das Ganze etwa 15-20 Minuten köcheln lassen, bis die Linsen weich sind. Zum Schluss den frischen Spinat unterrühren, bis er zusammenfällt, und mit Reis oder Naan-Brot servieren.', NULL),
    ('Nachspeise', 'Hausgemachtes Tiramisu', '500g Mascarpone, 3 Eier, 100g Zucker, 200g Löffelbiskuit, 250ml starker Espresso, ungesüßtes Kakaopulver', 'Eigelb mit Zucker schaumig schlagen, dann die Mascarpone unterrühren. Eiweiß steif schlagen und vorsichtig unter die Mascarpone-Creme heben. Die Löffelbiskuit kurz in den kalten Espresso tauchen und eine Form damit auslegen. Eine Schicht Creme darauf verteilen. Den Vorgang wiederholen und mit einer Cremeschicht abschließen. Vor dem Servieren dick mit Kakaopulver bestäuben und über Nacht kühlen.', NULL),
    ('Vorspeise', 'Griechischer Bauernsalat', '2 Gurken, 4 Tomaten, 1 rote Zwiebel, 200g Feta-Käse, eine Handvoll Kalamata-Oliven, getrockneter Oregano, Olivenöl', 'Gurken schälen und in grobe Stücke schneiden. Tomaten vierteln und die Zwiebel in feine Ringe schneiden. Alles in einer Schüssel mit den Oliven vermengen. Den Feta-Käse entweder würfeln oder im Ganzen oben auf den Salat legen. Reichlich Olivenöl darübergeben, mit Salz und Pfeffer abschmecken und großzügig mit getrocknetem Oregano bestreuen.', NULL),
    ('Hauptspeise', 'Zucchini-Paprika-Lasagne', 'Lasagneplatten, 2 Zucchini, 2 Paprika, 500ml passierte Tomaten, 1 Zwiebel, 250g Mozzarella, Parmesan, Béchamelsauce', 'Zuerst eine einfache Tomatensauce aus Zwiebeln, gewürfeltem Gemüse und passierten Tomaten kochen. In einer Auflaufform abwechselnd Sauce, Lasagneplatten und Béchamelsauce schichten. Die letzte Schicht sollte aus Bauschamel und reichlich geriebenem Mozzarella sowie Parmesan bestehen. Im vorgeheizten Ofen bei 200 Grad etwa 30-40 Minuten backen, bis die Kruste goldbraun und knusprig ist.', NULL),
    ('Vorspeise', 'Gefüllte Champignons', '12 große Champignons, 150g Kräuterfrischkäse, 50g geriebener Gouda, Knoblauch, frische Petersilie', 'Die Pilze vorsichtig säubern und die Stiele herausdrehen. Die Stiele fein hacken und mit dem Frischkäse, gepresstem Knoblauch und gehackter Petersilie vermengen. Die Masse mit einem kleinen Löffel in die Pilzköpfe füllen. Mit etwas geriebenem Käse bestreuen und in einer feuerfesten Form für ca. 15-20 Minuten bei 180 Grad backen, bis der Käse geschmolzen ist.', NULL),
    ('Nachspeise', 'Warmer Apfelkuchen', '4 Äpfel, 250g Mehl, 125g Butter, 100g Zucker, 1 Päckchen Backpulver, 3 Eier, Zimt', 'Butter mit Zucker und Eiern schaumig rühren. Mehl und Backpulver unterheben, bis ein glatter Teig entsteht. Die Äpfel schälen, entkernen und in Spalten schneiden. Den Teig in eine gefettete Form füllen und die Äpfel fächerförmig darauf verteilen. Mit etwas Zimt und Zucker bestreuen. Bei 180 Grad Ober-/Unterhitze etwa 45 Minuten backen, bis der Kuchen beim Stäbchentest sauber bleibt.', NULL),
    ('Hauptspeise', 'Gebratenes Lachsfilet', '2 Lachsfilets mit Haut, 1 Zitrone, frischer Dill, Butter, Meersalz', 'Den Lachs trocken tupfen und die Hautseite leicht mehlieren. In einer heißen Pfanne mit etwas Öl zuerst auf der Hautseite ca. 4 Minuten braten, bis diese knusprig ist. Dann vorsichtig wenden, ein Stück Butter, Zitronenscheiben und Dillzweige in die Pfanne geben und den Fisch weitere 2-3 Minuten fertig garen. Dabei den Lachs immer wieder mit der flüssigen Zitronenbutter übergießen.', NULL),
    ('Vorspeise', 'Kürbis-Ingwer-Suppe', '1kg Hokkaido-Kürbis, 1 Liter Gemüsebrühe, 1 Stück Ingwer, 200ml Sahne, Kürbiskerne zum Garnieren', 'Den Kürbis entkernen und in grobe Würfel schneiden (Hokkaido muss nicht geschält werden). Zusammen mit gehacktem Ingwer in etwas Öl anbraten, dann mit der Brühe ablöschen. Etwa 20 Minuten weich kochen. Die Suppe fein pürieren, die Sahne unterrühren und mit Salz, Pfeffer und einer Prise Muskat abschmecken. Mit gerösteten Kürbiskernen servieren.', NULL),
    ('Nachspeise', 'Panna Cotta mit Beeren', '500ml Sahne, 50g Zucker, 1 Vanilleschote, 3 Blatt Gelatine, 200g TK-Beerenmischung', 'Die Sahne mit Zucker und dem Mark der Vanilleschote langsam aufkochen und 10 Minuten köcheln lassen. Währenddessen Gelatine in kaltem Wasser einweichen. Die Sahne vom Herd nehmen und die ausgedrückte Gelatine darin auflösen. In Förmchen füllen und mindestens 5 Stunden kühlen. Die Beeren mit etwas Zucker einkochen, pürieren und als Fruchtspiegel über die gestürzte Panna Cotta geben.', NULL),
    ('Hauptspeise', 'Orientalische Shakshuka', '4 Eier, 1 Dose stückige Tomaten, 2 Paprikaschoten, 1 Zwiebel, Kreuzkümmel, Paprikapulver, frischer Koriander', 'Zwiebeln und gewürfelte Paprika in einer großen Pfanne kräftig anbraten. Die Tomaten und Gewürze hinzugeben und alles ca. 10 Minuten einkochen lassen, bis eine dicke Sauce entsteht. Mit einem Löffel vier Mulden in die Sauce drücken und die Eier vorsichtig hineinschlagen. Deckel auf die Pfanne setzen und bei mittlerer Hitze ca. 5-8 Minuten garen, bis das Eiweiß gestockt, das Eigelb aber noch flüssig ist.', NULL),
    ('Vorspeise', 'Frische Guacamole mit Nachos', '2 reife Avocados, 1 kleine Tomate, 1/2 rote Zwiebel, Saft einer Limette, Salz, Chili', 'Die Avocados halbieren, den Kern entfernen und das Fruchtfleisch mit einer Gabel in einer Schüssel fein zerdrücken. Die Tomate und Zwiebel extrem fein würfeln und unter das Avocado-Püree mischen. Sofort den Limettensaft hinzufügen, damit die Creme nicht braun wird. Mit Salz und einer Prise Chili abschmecken und direkt mit Mais-Nachos servieren.', NULL),
    ('Nachspeise', 'Gebackene Banane mit Honig', '3 reife Bananen, 2 EL Honig, Saft einer halben Zitrone, eine Handvoll Walnüsse', 'Die Bananen schälen und längs halbieren. In einer Pfanne etwas Butter schmelzen und die Bananen von beiden Seiten goldbraun anbraten. Mit Zitronensaft ablöschen und den Honig darübergeben, sodass er leicht karamellisiert. Die Bananen auf Tellern anrichten, mit der restlichen Honigsauce aus der Pfanne übergießen und mit gehackten Walnüssen bestreuen.', NULL);